import React from "react";
import styles from "../styles/tweetBox.module.scss";
import { Avatar, Button } from "@material-ui/core";

function TweetBox() {
  //1. Definimos el el texto y la imagen del Tweet
  const [tweetMessage, setTweetMessage] = useState("");
  const [tweetImage, setTweetImage] = useState("");

  const sendTweet = (e) => {
    e.preventDefault();
  };

  return (
    <div className={styles.tweetBox}>
      <form>
        <div className={styles.tweetBox__input}>
          <Avatar src="https://picsum.photos/200/300" />
          <input
            //2. Conectamos los states que definimos, aqui el text message del Tweet. OnChange fijamos el metodo que va a modificar el state de "TweetMessage" con lo que ingresemos en el input
            //2. We connect the states defined on item 1, here the text message of the tweet
            //OnChnage will setup the method to modify "T"
            onChange={(e) => setTweetMessage(e.target.value)}
            value={tweetMessage}
            placeholder="What's happening?"
            type="text"
          />
        </div>
        <input
        //The same that item 2
          value={tweetImage}
          onChange={(e) => setTweetImage(e.target.value)}
          className={styles.tweetBox__imageInput}
          placeholder="Enter image URL"
          type="text"
        />
        <Button
          onClick={sendTweet}
          type="submit"
          className={styles.tweetBox__tweetButton}
        >
          Tweet
        </Button>
      </form>
    </div>
  );
}

export default TweetBox;
