import React, { useState, useEffect } from "react";
import styles from "../styles/feed.module.scss";
import TweetBox from "../components/tweetBox";
import Post from "../components/post";
import db from "../firebase";

function Feed() {
  //1 Definim
  const [posts, setPosts] = useState([]);

  useEffect(() => {
    db.collection("post").onSnapshot((snapshot) =>
      setPosts(snapshot.docs.map((doc) => doc.data()))
    );
  }, []);
  return (
    <div className={styles.feed}>
      {/*Header */}
      <div className={styles.feed__header}>
        <h2>Home</h2>
      </div>

      {/*TweetBox */}
      <TweetBox />

      {/*Post */}
      {posts.map((post) => (
        <Post
          displayName="The Mandalorian  "
          username="Mando"
          verified={true}
          text="yooo it's working"
          avatar="https://lumiere-a.akamaihd.net/v1/images/5e97081f4e43710001cd36e6-image_40692e05.jpeg?region=0%2C0%2C1536%2C864"
          image="https://media.giphy.com/media/10AmJ6TIlbYxAk/giphy.gif"
        />
      ))}
    </div>
  );
}

export default Feed;
