import React from "react";
import styles from "../styles/post.module.scss";
import { Avatar, Button } from "@material-ui/core";
import VerifiedUserIcon from "@material-ui/icons/VerifiedUser";

function Post({ displayName, username, verified, text, image, avatar }) {
  return (
    <div className={styles.post}>
      <div className={styles.post__avatar}>
        <Avatar src="https://picsum.photos/200/300" />
      </div>
      <div className={styles.post__body}>
        <div className={styles.post__header}>
          <div className={styles.post__headerText}>
            <h3>
              Rocky Balboa{" "}
              <span>
                <VerifiedUserIcon className={styles.post__badge} />
              </span>
            </h3>
          </div>
          <div className={styles.post__headerDescription}>
            <p>Twitter clone</p>
          </div>
        </div>
        <img
          src="https://media.giphy.com/media/55SfA4BxofRBe/giphy.gif"
          alt=""
        />
        <div className={styles.post__footer}></div>
      </div>
    </div>
  );
}

export default Post;
